import React, { useState, useEffect } from 'react';
import { Calendar, Flag, Tag, AlertCircle } from 'lucide-react';
import '../assets/css/todoitem.css';

const TodoItem = ({ task, onUpdate, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState('');
  const [editedDueDate, setEditedDueDate] = useState('');
  const [editedPriority, setEditedPriority] = useState('medium');
  const [editedCategory, setEditedCategory] = useState('');

  // Update edited fields if task changes or on initial mount
  useEffect(() => {
    if (task) {
      setEditedTask(task.title || '');
      setEditedDueDate(task.dueDate || '');
      setEditedPriority(task.priority || 'medium');
      setEditedCategory(task.category || '');
    }
  }, [task]);

  // Handle the case where task is undefined in the render logic
  if (!task) {
    return (
      <div className="todo-item loading">
        <p>Loading...</p>
      </div>
    );
  }

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedTask(task.title);
    setEditedDueDate(task.dueDate || '');
    setEditedPriority(task.priority || 'medium');
    setEditedCategory(task.category || '');
  };

  const handleSave = () => {
    if (editedTask.trim()) {
      onUpdate(task.id, {
        ...task,
        title: editedTask,
        dueDate: editedDueDate,
        priority: editedPriority,
        category: editedCategory
      });
      setIsEditing(false);
    }
  };

  const handleChange = (e) => {
    setEditedTask(e.target.value);
  };

  const handleStatusToggle = () => {
    onUpdate(task.id, { ...task, completed: !task.completed });
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  // Get priority color
  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'text-red-500';
      case 'medium':
        return 'text-yellow-500';
      case 'low':
        return 'text-green-500';
      default:
        return 'text-gray-500';
    }
  };

  // Check if task is overdue
  const isOverdue = () => {
    if (!task.dueDate) return false;
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const dueDate = new Date(task.dueDate);
    return dueDate < today && !task.completed;
  };

  return (
    <div className={`todo-item ${task.completed ? 'completed' : ''} ${isOverdue() ? 'overdue' : ''}`}>
      {isEditing ? (
        <div className="todo-edit">
          <div className="edit-field">
            <label htmlFor="title">Task:</label>
            <input
              id="title"
              type="text"
              value={editedTask}
              onChange={handleChange}
              autoFocus
              className="edit-input"
            />
          </div>
          
          <div className="edit-field">
            <label htmlFor="dueDate">
              <Calendar size={16} className="inline-icon" />
              Due Date:
            </label>
            <input
              id="dueDate"
              type="date"
              value={editedDueDate}
              onChange={(e) => setEditedDueDate(e.target.value)}
              className="date-input"
            />
          </div>
          
          <div className="edit-field">
            <label htmlFor="priority">
              <Flag size={16} className="inline-icon" />
              Priority:
            </label>
            <select
              id="priority"
              value={editedPriority}
              onChange={(e) => setEditedPriority(e.target.value)}
              className="priority-select"
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
          </div>
          
          <div className="edit-field">
            <label htmlFor="category">
              <Tag size={16} className="inline-icon" />
              Category:
            </label>
            <input
              id="category"
              type="text"
              value={editedCategory}
              onChange={(e) => setEditedCategory(e.target.value)}
              placeholder="e.g., Work, Personal, Shopping"
              className="category-input"
            />
          </div>
          
          <div className="edit-actions">
            <button onClick={handleSave} className="save-btn">Save</button>
            <button onClick={handleCancel} className="cancel-btn">Cancel</button>
          </div>
        </div>
      ) : (
        <>
          <div className="todo-content">
            <input
              type="checkbox"
              checked={task.completed}
              onChange={handleStatusToggle}
              className="todo-checkbox"
            />
            <div className="todo-details">
              <span className="todo-title">{task.title}</span>
              
              <div className="todo-metadata">
                {task.dueDate && (
                  <span className={`todo-due-date ${isOverdue() ? 'text-red-500' : ''}`}>
                    <Calendar size={14} className="inline-icon" />
                    {formatDate(task.dueDate)}
                    {isOverdue() && <AlertCircle size={14} className="ml-1 inline-icon text-red-500" />}
                  </span>
                )}
                
                {task.priority && (
                  <span className={`todo-priority ${getPriorityColor(task.priority)}`}>
                    <Flag size={14} className="inline-icon" />
                    {task.priority.charAt(0).toUpperCase() + task.priority.slice(1)}
                  </span>
                )}
                
                {task.category && (
                  <span className="todo-category">
                    <Tag size={14} className="inline-icon" />
                    {task.category}
                  </span>
                )}
              </div>
            </div>
          </div>
          
          <div className="todo-actions">
            <button onClick={handleEdit} className="edit-btn">Edit</button>
            <button onClick={() => onDelete(task.id)} className="delete-btn">Delete</button>
          </div>
        </>
      )}
    </div>
  );
};

// Add default props
TodoItem.defaultProps = {
  task: null,
  onUpdate: () => {},
  onDelete: () => {}
};

export default TodoItem;
