import React, { useState, useEffect } from 'react';
import { Calendar, Flag, Tag, AlertCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import '../assets/css/todoitem.css';

const TodoItem = () => {
  const { getTasks } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const tasksData = await getTasks();
      setTasks(tasksData);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch tasks');
      setLoading(false);
    }
  };

  const handleUpdate = async (id, updatedTask) => {
    try {
      const updatedTasks = tasks.map(task => 
        task.id === id ? { ...task, ...updatedTask } : task
      );
      setTasks(updatedTasks);
    } catch (err) {
      setError('Failed to update task');
    }
  };

  const handleDelete = async (id) => {
    try {
      setTasks(tasks.filter(task => task.id !== id));
    } catch (err) {
      setError('Failed to delete task');
    }
  };

  if (loading) {
    return <div className="todo-list-loading">Loading tasks...</div>;
  }

  if (error) {
    return <div className="todo-list-error">{error}</div>;
  }

  return (
    <div className="todo-list">
      {tasks.length === 0 ? (
        <div className="no-tasks">No tasks found. Add a task to get started!</div>
      ) : (
        tasks.map(task => (
          <div key={task.id} className={`todo-item ${task.completed ? 'completed' : ''}`}>
            <div className="todo-content">
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleUpdate(task.id, { completed: !task.completed })}
                className="todo-checkbox"
              />
              <div className="todo-details">
                <span className="todo-title">{task.title}</span>
                
                <div className="todo-metadata">
                  {task.dueDate && (
                    <span className={`todo-due-date ${new Date(task.dueDate) < new Date() && !task.completed ? 'text-red-500' : ''}`}>
                      <Calendar size={14} className="inline-icon" />
                      {new Date(task.dueDate).toLocaleDateString()}
                      {new Date(task.dueDate) < new Date() && !task.completed && <AlertCircle size={14} className="ml-1 inline-icon text-red-500" />}
                    </span>
                  )}
                  
                  {task.priority && (
                    <span className={`todo-priority ${task.priority === 'high' ? 'text-red-500' : task.priority === 'medium' ? 'text-yellow-500' : 'text-green-500'}`}>
                      <Flag size={14} className="inline-icon" />
                      {task.priority.charAt(0).toUpperCase() + task.priority.slice(1)}
                    </span>
                  )}
                  
                  {task.category && (
                    <span className="todo-category">
                      <Tag size={14} className="inline-icon" />
                      {task.category}
                    </span>
                  )}
                </div>
              </div>
            </div>
            
            <div className="todo-actions">
              <button onClick={() => handleUpdate(task.id, { title: task.title })} className="edit-btn">Edit</button>
              <button onClick={() => handleDelete(task.id)} className="delete-btn">Delete</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default TodoItem;
