import React, { useState, useEffect } from 'react';
import '../assets/css/todoitem.css';

const TodoItem = ({ task, onUpdate, onDelete }) => {
  // Add a check for task existence
  if (!task) {
    return null; // Or return a placeholder/loading state
  }

  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(task.title);
  
  // Update editedTask if task changes
  useEffect(() => {
    if (task?.title) {
      setEditedTask(task.title);
    }
  }, [task]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedTask(task.title);
  };

  const handleSave = () => {
    if (editedTask.trim()) {
      onUpdate(task.id, { ...task, title: editedTask });
      setIsEditing(false);
    }
  };

  const handleChange = (e) => {
    setEditedTask(e.target.value);
  };

  const handleStatusToggle = () => {
    onUpdate(task.id, { ...task, completed: !task.completed });
  };

  return (
    <div className={`todo-item ${task.completed ? 'completed' : ''}`}>
      {isEditing ? (
        <div className="todo-edit">
          <input
            type="text"
            value={editedTask}
            onChange={handleChange}
            autoFocus
            className="edit-input"
          />
          <div className="edit-actions">
            <button onClick={handleSave} className="save-btn">Save</button>
            <button onClick={handleCancel} className="cancel-btn">Cancel</button>
          </div>
        </div>
      ) : (
        <>
          <div className="todo-content">
            <input
              type="checkbox"
              checked={task.completed}
              onChange={handleStatusToggle}
              className="todo-checkbox"
            />
            <span className="todo-title">{task.title}</span>
          </div>
          <div className="todo-actions">
            <button onClick={handleEdit} className="edit-btn">Edit</button>
            <button onClick={() => onDelete(task.id)} className="delete-btn">Delete</button>
          </div>
        </>
      )}
    </div>
  );
};

// Add default props to ensure task is always defined
TodoItem.defaultProps = {
  task: null,
  onUpdate: () => {},
  onDelete: () => {}
};

export default TodoItem;
